import React, { useState, useEffect } from 'react';
import axios from 'axios';

function StaffManagement() {
  const [staff, setStaff] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAddForm, setShowAddForm] = useState(false);
  const [showScheduleForm, setShowScheduleForm] = useState(false);
  const [showPerformanceForm, setShowPerformanceForm] = useState(false);
  const [schedules, setSchedules] = useState([]);
  const [newStaff, setNewStaff] = useState({
    name: '',
    email: '',
    role: 'nurse',
    department: 'emergency'
  });
  const [selectedStaff, setSelectedStaff] = useState('');
  const [scheduleDate, setScheduleDate] = useState('');
  const [scheduleShift, setScheduleShift] = useState('morning');

  useEffect(() => {
    loadStaff();
  }, []);

  const loadStaff = async () => {
    try {
      const response = await axios.get('/api/staff');
      setStaff(response.data);
    } catch (error) {
      console.error('Error loading staff:', error);
      setStaff([]);
    } finally {
      setLoading(false);
    }
  };

  const loadSchedules = async () => {
    try {
      const response = await axios.get('/api/schedules');
      setSchedules(response.data);
    } catch (error) {
      console.error('Error loading schedules:', error);
      setSchedules([]);
    }
  };

  const handleAddStaff = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/auth/register', newStaff);
      setNewStaff({
        name: '',
        email: '',
        role: 'nurse',
        department: 'emergency'
      });
      setShowAddForm(false);
      loadStaff(); // Refresh the staff list
    } catch (error) {
      console.error('Error adding staff:', error);
      alert('Failed to add staff member');
    }
  };

  const handleCreateSchedule = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/schedules', {
        staffId: selectedStaff,
        date: scheduleDate,
        shift: scheduleShift
      });
      setSelectedStaff('');
      setScheduleDate('');
      setScheduleShift('morning');
      setShowScheduleForm(false);
      loadSchedules();
      alert('Schedule created successfully!');
    } catch (error) {
      console.error('Error creating schedule:', error);
      alert('Failed to create schedule');
    }
  };

  const handleViewPerformance = () => {
    // Generate performance report
    const report = generatePerformanceReport();
    alert(report);
  };

  const generatePerformanceReport = () => {
    const totalStaff = staff.length;
    const activeSchedules = schedules.filter(s => 
      new Date(s.date) >= new Date()
    ).length;
    
    return `Performance Report:\n\nTotal Staff: ${totalStaff}\nActive Schedules: ${activeSchedules}\n\nStaff Distribution:\n- Doctors: ${staff.filter(s => s.role === 'doctor').length}\n- Nurses: ${staff.filter(s => s.role === 'nurse').length}\n- Paramedics: ${staff.filter(s => s.role === 'paramedic').length}\n- Admins: ${staff.filter(s => s.role === 'admin').length}`;
  };

  if (loading) return <div className="loading">Loading staff...</div>;

  const staffByRole = {
    admin: staff.filter(s => s.role === 'admin'),
    doctor: staff.filter(s => s.role === 'doctor'),
    nurse: staff.filter(s => s.role === 'nurse'),
    paramedic: staff.filter(s => s.role === 'paramedic')
  };

  return (
    <div className="staff-management">
      <h2>Staff Management</h2>

      <div className="staff-summary">
        <div className="staff-stat">
          <h3>Total Staff</h3>
          <div className="stat-value">{staff.length}</div>
        </div>
        <div className="staff-stat">
          <h3>Doctors</h3>
          <div className="stat-value">{staffByRole.doctor.length}</div>
        </div>
        <div className="staff-stat">
          <h3>Nurses</h3>
          <div className="stat-value">{staffByRole.nurse.length}</div>
        </div>
        <div className="staff-stat">
          <h3>Paramedics</h3>
          <div className="stat-value">{staffByRole.paramedic.length}</div>
        </div>
      </div>

      <div className="staff-list">
        <h3>Staff Directory</h3>
        {Object.entries(staffByRole).map(([role, staffList]) => (
          <div key={role} className="staff-role-section">
            <h4>{role.charAt(0).toUpperCase() + role.slice(1)}s ({staffList.length})</h4>
            <div className="staff-grid">
              {staffList.map(person => (
                <div key={person._id} className="staff-card">
                  <div className="staff-name">{person.name}</div>
                  <div className="staff-details">
                    <span className="staff-dept">{person.department}</span>
                    <span className="staff-email">{person.email}</span>
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>

      {showAddForm && (
        <div className="add-staff-form">
          <h3>Add New Staff Member</h3>
          <form onSubmit={handleAddStaff}>
            <div className="form-group">
              <label>Full Name:</label>
              <input
                type="text"
                value={newStaff.name}
                onChange={(e) => setNewStaff({...newStaff, name: e.target.value})}
                required
                placeholder="Enter full name"
              />
            </div>
            <div className="form-group">
              <label>Email:</label>
              <input
                type="email"
                value={newStaff.email}
                onChange={(e) => setNewStaff({...newStaff, email: e.target.value})}
                required
                placeholder="Enter email address"
              />
            </div>
            <div className="form-group">
              <label>Role:</label>
              <select
                value={newStaff.role}
                onChange={(e) => setNewStaff({...newStaff, role: e.target.value})}
                required
              >
                <option value="admin">Admin</option>
                <option value="doctor">Doctor</option>
                <option value="nurse">Nurse</option>
                <option value="paramedic">Paramedic</option>
              </select>
            </div>
            <div className="form-group">
              <label>Department:</label>
              <select
                value={newStaff.department}
                onChange={(e) => setNewStaff({...newStaff, department: e.target.value})}
                required
              >
                <option value="emergency">Emergency</option>
                <option value="surgery">Surgery</option>
                <option value="cardiology">Cardiology</option>
                <option value="pediatrics">Pediatrics</option>
                <option value="icu">ICU</option>
              </select>
            </div>
            <div className="form-actions">
              <button type="submit" className="add-btn">Add Staff Member</button>
              <button type="button" onClick={() => setShowAddForm(false)} className="cancel-btn">Cancel</button>
            </div>
          </form>
        </div>
      )}

      <div className="staff-actions">
        <button className="staff-btn" onClick={() => setShowAddForm(true)}>Add New Staff</button>
        <button className="staff-btn" onClick={() => alert('Schedule Management feature coming soon!')}>Schedule Management</button>
        <button className="staff-btn" onClick={() => alert('Performance Reports feature coming soon!')}>Performance Reports</button>
      </div>
    </div>
  );
}

export default StaffManagement;
