}

// Resource Management Component
function ResourceManagement() {
  const [beds, setBeds] = useState([]);
  const [ambulances, setAmbulances] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadResources();
  }, []);

  const loadResources = async () => {
    try {
      const [bedsRes, ambulancesRes] = await Promise.all([
        axios.get('/api/beds'),
        axios.get('/api/ambulances')
      ]);
      setBeds(bedsRes.data);
      setAmbulances(ambulancesRes.data);
    } catch (error) {
      console.error('Error loading resources:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div className="loading">Loading resources...</div>;

  const bedStats = {
    total: beds.length,
    available: beds.filter(b => b.status === 'available').length,
    occupied: beds.filter(b => b.status === 'occupied').length,
    maintenance: beds.filter(b => b.status === 'maintenance').length
  };

  const ambulanceStats = {
    total: ambulances.length,
    available: ambulances.filter(a => a.status === 'available').length,
    onCall: ambulances.filter(a => a.status === 'on_call').length,
    maintenance: ambulances.filter(a => a.status === 'maintenance').length
  };

  return (
    <div className="resource-management">
      <h2>Resource Management</h2>

      <div className="resource-overview">
        <div className="resource-section">
          <h3>Bed Status</h3>
          <div className="resource-stats">
            <div className="stat-item">
              <span className="stat-label">Total Beds:</span>
              <span className="stat-value">{bedStats.total}</span>
            </div>
            <div className="stat-item">
              <span className="stat-label">Available:</span>
              <span className="stat-value available">{bedStats.available}</span>
            </div>
            <div className="stat-item">
              <span className="stat-label">Occupied:</span>
              <span className="stat-value occupied">{bedStats.occupied}</span>
            </div>
            <div className="stat-item">
              <span className="stat-label">Maintenance:</span>
              <span className="stat-value maintenance">{bedStats.maintenance}</span>
            </div>
          </div>
        </div>

        <div className="resource-section">
          <h3>Ambulance Status</h3>
          <div className="resource-stats">
            <div className="stat-item">
              <span className="stat-label">Total Units:</span>
              <span className="stat-value">{ambulanceStats.total}</span>
            </div>
            <div className="stat-item">
              <span className="stat-label">Available:</span>
              <span className="stat-value available">{ambulanceStats.available}</span>
            </div>
            <div className="stat-item">
              <span className="stat-label">On Call:</span>
              <span className="stat-value on-call">{ambulanceStats.onCall}</span>
            </div>
            <div className="stat-item">
              <span className="stat-label">Maintenance:</span>
              <span className="stat-value maintenance">{ambulanceStats.maintenance}</span>
            </div>
          </div>
        </div>
      </div>

      <div className="resource-actions">
        <button className="resource-btn">Add New Bed</button>
        <button className="resource-btn">Add Ambulance</button>
        <button className="resource-btn">Maintenance Schedule</button>
      </div>
    </div>
  );
}

// Alert Management Component
function AlertManagement() {
  const [alerts, setAlerts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadAlerts();
  }, []);

  const loadAlerts = async () => {
    try {
      const response = await axios.get('/api/alerts');
      setAlerts(response.data);
    } catch (error) {
      console.error('Error loading alerts:', error);
      setAlerts([]);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div className="loading">Loading alerts...</div>;

  const activeAlerts = alerts.filter(a => a.status === 'active');
  const resolvedAlerts = alerts.filter(a => a.status === 'resolved');

  return (
    <div className="alert-management">
      <h2>Alert Management</h2>

      <div className="alert-summary">
        <div className="alert-stat">
          <h3>Active Alerts</h3>
          <div className="stat-value alert-count">{activeAlerts.length}</div>
        </div>
        <div className="alert-stat">
          <h3>Resolved Today</h3>
          <div className="stat-value">{resolvedAlerts.filter(a =>
            new Date(a.createdAt).toDateString() === new Date().toDateString()
          ).length}</div>
        </div>
      </div>

      <div className="alerts-list-admin">
        <h3>All Alerts</h3>
        {alerts.map(alert => (
          <div key={alert._id} className={`alert-item-admin ${alert.status}`}>
            <div className="alert-header-admin">
              <span className="alert-type-admin">{alert.type.replace('_', ' ').toUpperCase()}</span>
              <span className="alert-severity-admin">{alert.severity}</span>
              <span className="alert-status-admin">{alert.status}</span>
              <span className="alert-time-admin">
                {format(new Date(alert.createdAt), 'MMM dd, HH:mm')}
              </span>
            </div>
            <div className="alert-content-admin">
              <h4>{alert.title}</h4>
              <p>{alert.message}</p>
            </div>
          </div>
        ))}
        {alerts.length === 0 && (
          <div className="no-alerts">No alerts found</div>
        )}
      </div>

      <div className="alert-actions">
        <button className="alert-btn">Create New Alert</button>
        <button className="alert-btn">Emergency Broadcast</button>
      </div>
    </div>
  );
}

