import React, { useState, useEffect, createContext, useContext, useCallback } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import axios from 'axios';
import { io } from 'socket.io-client';
import { format } from 'date-fns';
import './App.css';

// Context for authentication
const AuthContext = createContext();

// Auth Provider Component
function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      checkAuthStatus();
    } else {
      setLoading(false);
    }
  }, []);

  const checkAuthStatus = async () => {
    try {
      const response = await axios.get('/api/auth/me');
      setUser(response.data.user);
    } catch (error) {
      localStorage.removeItem('token');
      delete axios.defaults.headers.common['Authorization'];
    } finally {
      setLoading(false);
    }
  };

  const login = async (email, password) => {
    const response = await axios.post('/api/auth/login', { email, password });
    const { token, user } = response.data;

    localStorage.setItem('token', token);
    axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    setUser(user);

    return user;
  };

  const logout = () => {
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
    setUser(null);
  };

  return (
    <AuthContext.Provider value={{ user, login, logout, loading }}>
      {children}
    </AuthContext.Provider>
  );
}

// Custom hook for auth
const useAuth = () => useContext(AuthContext);

// Login Component
function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const { login } = useAuth();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      await login(email, password);
    } catch (error) {
      setError(error.response?.data?.error || 'Login failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="login-form">
        <h2>Hospital Emergency Intelligence System</h2>
        <p>Staff Login Portal</p>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>

          <div className="form-group">
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>

          {error && <div className="error-message">{error}</div>}

          <button type="submit" disabled={loading} className="login-btn">
            {loading ? 'Logging in...' : 'Login'}
          </button>
        </form>

        <div className="demo-accounts">
          <h3>Demo Accounts:</h3>
          <div className="account-info">
            <strong>Admin:</strong> admin@hospital.com / admin123
          </div>
          <div className="account-info">
            <strong>Doctor:</strong> doctor@hospital.com / doctor123
          </div>
          <div className="account-info">
            <strong>Nurse:</strong> nurse@hospital.com / nurse123
          </div>
        </div>
      </div>
    </div>
  );
}

// Protected Route Component
function ProtectedRoute({ children, allowedRoles = [] }) {
  const { user, loading } = useAuth();

  if (loading) return <div className="loading">Loading...</div>;
  if (!user) return <Navigate to="/login" />;
  if (allowedRoles.length > 0 && !allowedRoles.includes(user.role)) {
    return <Navigate to="/unauthorized" />;
  }

  return children;
}

// Main App Component
function App() {
  return (
    <AuthProvider>
      <Router future={{ v7_startTransition: true, v7_relativeSplatPath: true }}>
        <div className="App">
          <Routes>
            <Route path="/login" element={<Login />} />
            <Route path="/unauthorized" element={<div className="unauthorized">Access Denied</div>} />
            <Route path="/" element={
              <ProtectedRoute>
                <Dashboard />
              </ProtectedRoute>
            } />
            <Route path="/admin/*" element={
              <ProtectedRoute allowedRoles={['admin']}>
                <AdminDashboard />
              </ProtectedRoute>
            } />
          </Routes>
        </div>
      </Router>
    </AuthProvider>
  );
}

// Dashboard Component (Staff View)
function Dashboard() {
  const { user, logout } = useAuth();
  const [activeTab, setActiveTab] = useState('overview');
  const [alerts, setAlerts] = useState([]);
  const [schedules, setSchedules] = useState([]);

  useEffect(() => {
    // Connect to Socket.IO
    const newSocket = io('http://localhost:3001');

    // Join user room for alerts
    newSocket.emit('join-alerts', user.id);

    // Listen for new alerts
    newSocket.on('new-alert', (alert) => {
      setAlerts(prev => [alert, ...prev]);
    });

    // Load initial data
    loadAlerts();
    loadSchedules();

    return () => newSocket.close();
  }, [user.id]);

  const loadAlerts = async () => {
    try {
      const response = await axios.get('/api/alerts');
      setAlerts(response.data);
    } catch (error) {
      console.error('Error loading alerts:', error);
    }
  };

  const loadSchedules = async () => {
    try {
      const response = await axios.get('/api/schedules');
      setSchedules(response.data);
    } catch (error) {
      console.error('Error loading schedules:', error);
    }
  };

  const acknowledgeAlert = async (alertId) => {
    try {
      await axios.put(`/api/alerts/${alertId}/acknowledge`);
      setAlerts(prev => prev.map(alert =>
        alert._id === alertId
          ? { ...alert, acknowledgedBy: [...alert.acknowledgedBy, { userId: user.id }] }
          : alert
      ));
    } catch (error) {
      console.error('Error acknowledging alert:', error);
    }
  };

  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <h1>Hospital Emergency Intelligence</h1>
        <div className="user-info">
          <span>Welcome, {user.name} ({user.role})</span>
          <button onClick={logout} className="logout-btn">Logout</button>
        </div>
      </header>

      <nav className="dashboard-nav">
        <button
          className={activeTab === 'overview' ? 'nav-btn active' : 'nav-btn'}
          onClick={() => setActiveTab('overview')}
        >
          Overview
        </button>
        <button
          className={activeTab === 'alerts' ? 'nav-btn active' : 'nav-btn'}
          onClick={() => setActiveTab('alerts')}
        >
          Alerts {alerts.filter(a => a.status === 'active').length > 0 && `(${alerts.filter(a => a.status === 'active').length})`}
        </button>
        <button
          className={activeTab === 'schedule' ? 'nav-btn active' : 'nav-btn'}
          onClick={() => setActiveTab('schedule')}
        >
          My Schedule
        </button>
        {user.role === 'admin' && (
          <button
            className={activeTab === 'admin' ? 'nav-btn active' : 'nav-btn'}
            onClick={() => setActiveTab('admin')}
          >
            Admin Panel
          </button>
        )}
      </nav>

      <main className="dashboard-content">
        {activeTab === 'overview' && <OverviewTab user={user} />}
        {activeTab === 'alerts' && (
          <AlertsTab
            alerts={alerts}
            onAcknowledge={acknowledgeAlert}
            user={user}
          />
        )}
        {activeTab === 'schedule' && <ScheduleTab schedules={schedules} />}
        {activeTab === 'admin' && user.role === 'admin' && <AdminPanelTab />}
      </main>
    </div>
  );
}

// Overview Tab Component
function OverviewTab({ user }) {
  const [stats, setStats] = useState({
    activeAlerts: 0,
    todaysSchedules: 0,
    department: user.department
  });

  const loadStats = useCallback(async () => {
    try {
      const [alertsRes, schedulesRes] = await Promise.all([
        axios.get('/api/alerts?status=active'),
        axios.get('/api/schedules')
      ]);

      setStats({
        activeAlerts: alertsRes.data.length,
        todaysSchedules: schedulesRes.data.filter(s =>
          new Date(s.date).toDateString() === new Date().toDateString()
        ).length,
        department: user.department
      });
    } catch (error) {
      console.error('Error loading stats:', error);
    }
  }, [user]);

  useEffect(() => {
    loadStats();
  }, [loadStats]);

  return (
    <div className="overview-tab">
      <h2>Dashboard Overview</h2>

      <div className="stats-grid">
        <div className="stat-card">
          <h3>Active Alerts</h3>
          <div className="stat-value">{stats.activeAlerts}</div>
          <p>Require attention</p>
        </div>

        <div className="stat-card">
          <h3>Today's Schedules</h3>
          <div className="stat-value">{stats.todaysSchedules}</div>
          <p>Shifts assigned</p>
        </div>

        <div className="stat-card">
          <h3>Department</h3>
          <div className="stat-value">{stats.department}</div>
          <p>Your unit</p>
        </div>

        <div className="stat-card">
          <h3>Status</h3>
          <div className="stat-value status-active">Active</div>
          <p>Current status</p>
        </div>
      </div>

      <div className="quick-actions">
        <h3>Quick Actions</h3>
        <div className="action-buttons">
          <button className="action-btn">View Emergency Load</button>
          <button className="action-btn">Check Resources</button>
          <button className="action-btn">Contact Admin</button>
        </div>
      </div>
    </div>
  );
}

// Alerts Tab Component
function AlertsTab({ alerts, onAcknowledge, user }) {
  const activeAlerts = alerts.filter(alert => alert.status === 'active');
  const acknowledgedAlerts = alerts.filter(alert =>
    alert.acknowledgedBy.some(ack => ack.userId === user.id)
  );

  return (
    <div className="alerts-tab">
      <h2>Emergency Alerts</h2>

      <div className="alerts-section">
        <h3>Active Alerts ({activeAlerts.length})</h3>
        <div className="alerts-list">
          {activeAlerts.map(alert => (
            <div key={alert._id} className={`alert-item ${alert.severity}`}>
              <div className="alert-header">
                <span className="alert-type">{alert.type.replace('_', ' ').toUpperCase()}</span>
                <span className="alert-severity">{alert.severity}</span>
                <span className="alert-time">
                  {format(new Date(alert.createdAt), 'MMM dd, HH:mm')}
                </span>
              </div>
              <div className="alert-content">
                <h4>{alert.title}</h4>
                <p>{alert.message}</p>
                {alert.recommendedActions && alert.recommendedActions.length > 0 && (
                  <div className="alert-actions">
                    <h5>Recommended Actions:</h5>
                    <ul>
                      {alert.recommendedActions.map((action, index) => (
                        <li key={index}>{action.action}</li>
                      ))}
                    </ul>
                  </div>
                )}
                <button
                  onClick={() => onAcknowledge(alert._id)}
                  className="acknowledge-btn"
                  disabled={alert.acknowledgedBy.some(ack => ack.userId === user.id)}
                >
                  {alert.acknowledgedBy.some(ack => ack.userId === user.id) ? 'Acknowledged' : 'Acknowledge'}
                </button>
              </div>
            </div>
          ))}
          {activeAlerts.length === 0 && (
            <div className="no-alerts">No active alerts</div>
          )}
        </div>
      </div>

      <div className="alerts-section">
        <h3>Acknowledged Alerts ({acknowledgedAlerts.length})</h3>
        <div className="alerts-list">
          {acknowledgedAlerts.slice(0, 5).map(alert => (
            <div key={alert._id} className="alert-item acknowledged">
              <div className="alert-header">
                <span className="alert-type">{alert.type.replace('_', ' ').toUpperCase()}</span>
                <span className="alert-time">
                  {format(new Date(alert.createdAt), 'MMM dd, HH:mm')}
                </span>
              </div>
              <div className="alert-content">
                <h4>{alert.title}</h4>
                <p>{alert.message}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

// Schedule Tab Component
function ScheduleTab({ schedules }) {
  const today = new Date();
  const todaysSchedules = schedules.filter(schedule =>
    new Date(schedule.date).toDateString() === today.toDateString()
  );

  const upcomingSchedules = schedules.filter(schedule =>
    new Date(schedule.date) > today
  ).slice(0, 5);

  return (
    <div className="schedule-tab">
      <h2>My Schedule</h2>

      <div className="schedule-section">
        <h3>Today's Schedule</h3>
        <div className="schedule-list">
          {todaysSchedules.map(schedule => (
            <div key={schedule._id} className="schedule-item">
              <div className="schedule-time">
                {schedule.startTime} - {schedule.endTime}
              </div>
              <div className="schedule-details">
                <span className="shift">{schedule.shift}</span>
                <span className="department">{schedule.department}</span>
                <span className={`status ${schedule.status}`}>{schedule.status}</span>
              </div>
              {schedule.notes && (
                <div className="schedule-notes">{schedule.notes}</div>
              )}
            </div>
          ))}
          {todaysSchedules.length === 0 && (
            <div className="no-schedule">No schedules for today</div>
          )}
        </div>
      </div>

      <div className="schedule-section">
        <h3>Upcoming Schedules</h3>
        <div className="schedule-list">
          {upcomingSchedules.map(schedule => (
            <div key={schedule._id} className="schedule-item">
              <div className="schedule-date">
                {format(new Date(schedule.date), 'MMM dd, yyyy')}
              </div>
              <div className="schedule-time">
                {schedule.startTime} - {schedule.endTime}
              </div>
              <div className="schedule-details">
                <span className="shift">{schedule.shift}</span>
                <span className="department">{schedule.department}</span>
              </div>
            </div>
          ))}
          {upcomingSchedules.length === 0 && (
            <div className="no-schedule">No upcoming schedules</div>
          )}
        </div>
      </div>
    </div>
  );
}

// Admin Panel Tab Component (for admin users)
function AdminPanelTab() {
  return (
    <div className="admin-panel-tab">
      <h2>Admin Quick Access</h2>
      <div className="admin-actions">
        <button
          className="admin-btn"
          onClick={() => window.location.href = '/admin'}
        >
          Full Admin Dashboard
        </button>
        <button className="admin-btn">Manage Staff</button>
        <button className="admin-btn">Resource Management</button>
        <button className="admin-btn">Create Alert</button>
      </div>
    </div>
  );
}

// Admin Dashboard Component (separate route)
function AdminDashboard() {
  const { user, logout } = useAuth();
  const [activeSection, setActiveSection] = useState('overview');

  return (
    <div className="admin-dashboard">
      <header className="admin-header">
        <h1>Admin Dashboard - Hospital Emergency Intelligence</h1>
        <div className="admin-user-info">
          <span>Admin: {user.name}</span>
          <button onClick={() => window.location.href = '/'} className="back-btn">Back to Staff View</button>
          <button onClick={logout} className="logout-btn">Logout</button>
        </div>
      </header>

      <nav className="admin-nav">
        <button
          className={activeSection === 'overview' ? 'nav-btn active' : 'nav-btn'}
          onClick={() => setActiveSection('overview')}
        >
          Overview
        </button>
        <button
          className={activeSection === 'staff' ? 'nav-btn active' : 'nav-btn'}
          onClick={() => setActiveSection('staff')}
        >
          Staff Management
        </button>
        <button
          className={activeSection === 'resources' ? 'nav-btn active' : 'nav-btn'}
          onClick={() => setActiveSection('resources')}
        >
          Resources
        </button>
        <button
          className={activeSection === 'alerts' ? 'nav-btn active' : 'nav-btn'}
          onClick={() => setActiveSection('alerts')}
        >
          Alert Management
        </button>
        <button
          className={activeSection === 'analytics' ? 'nav-btn active' : 'nav-btn'}
          onClick={() => setActiveSection('analytics')}
        >
          Analytics
        </button>
      </nav>

      <main className="admin-content">
        {activeSection === 'overview' && <AdminOverview />}
        {activeSection === 'staff' && <StaffManagement />}
        {activeSection === 'resources' && <ResourceManagement />}
        {activeSection === 'alerts' && <AlertManagement />}
        {activeSection === 'analytics' && <AdminAnalytics />}
      </main>
    </div>
  );
}

// Admin Overview Component
function AdminOverview() {
  const [stats, setStats] = useState({
    totalStaff: 0,
    activeAlerts: 0,
    availableBeds: 0,
    availableAmbulances: 0
  });

  const loadStats = useCallback(async () => {
    try {
      const [staffRes, alertsRes, bedsRes, ambulancesRes] = await Promise.all([
        axios.get('/api/staff'),
        axios.get('/api/alerts?status=active'),
        axios.get('/api/beds?status=available'),
        axios.get('/api/ambulances?status=available')
      ]);

      setStats({
        totalStaff: staffRes.data.length,
        activeAlerts: alertsRes.data.length,
        availableBeds: bedsRes.data.length,
        availableAmbulances: ambulancesRes.data.length
      });
    } catch (error) {
      console.error('Error loading admin stats:', error);
    }
  }, []);

  useEffect(() => {
    loadStats();
  }, [loadStats]);

  return (
    <div className="admin-overview">
      <h2>System Overview</h2>

      <div className="admin-stats-grid">
        <div className="admin-stat-card">
          <h3>Total Staff</h3>
          <div className="stat-value">{stats.totalStaff}</div>
        </div>

        <div className="admin-stat-card">
          <h3>Active Alerts</h3>
          <div className="stat-value alert-count">{stats.activeAlerts}</div>
        </div>

        <div className="admin-stat-card">
          <h3>Available Beds</h3>
          <div className="stat-value">{stats.availableBeds}</div>
        </div>

        <div className="admin-stat-card">
          <h3>Available Ambulances</h3>
          <div className="stat-value">{stats.availableAmbulances}</div>
        </div>
      </div>

      <div className="quick-admin-actions">
        <h3>Quick Actions</h3>
        <div className="admin-action-grid">
          <button className="admin-action-btn">Create Emergency Alert</button>
          <button className="admin-action-btn">Schedule Staff</button>
          <button className="admin-action-btn">Check Resources</button>
          <button className="admin-action-btn">Generate Report</button>
        </div>
      </div>
    </div>
  );
}

// Placeholder components for other admin sections
function StaffManagement() {
  return <div className="admin-section"><h2>Staff Management</h2><p>Staff management interface coming soon...</p></div>;
}

function ResourceManagement() {
  return <div className="admin-section"><h2>Resource Management</h2><p>Resource management interface coming soon...</p></div>;
}

function AlertManagement() {
  return <div className="admin-section"><h2>Alert Management</h2><p>Alert management interface coming soon...</p></div>;
}

function AdminAnalytics() {
  return <div className="admin-section"><h2>Analytics Dashboard</h2><p>Advanced analytics coming soon...</p></div>;
}

export default App;
