import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Modal from '../Modal';

function AreaManagement() {
  const [areas, setAreas] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAddModal, setShowAddModal] = useState(false);
  const [newArea, setNewArea] = useState({
    name: '',
    region: 'central',
    population: '',
    hospitals: [],
    riskFactors: {
      trafficDensity: 5,
      industrialActivity: 5,
      elderlyPopulation: 15,
      medicalFacilities: 5,
      crimeRate: 5
    },
    coordinates: {
      type: 'Polygon',
      coordinates: []
    },
    emergencyContacts: []
  });

  useEffect(() => {
    loadAreas();
  }, []);

  const loadAreas = async () => {
    try {
      const response = await axios.get('/api/areas');
      setAreas(response.data);
    } catch (error) {
      console.error('Error loading areas:', error);
      setAreas([]);
    } finally {
      setLoading(false);
    }
  };

  const handleAddArea = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/areas', newArea);
      setNewArea({
        name: '',
        description: '',
        population: '',
        emergencyServices: [],
        coordinates: {
          lat: '',
          lng: ''
        }
      });
      setShowAddModal(false);
      loadAreas();
      alert('Area added successfully!');
    } catch (error) {
      console.error('Error adding area:', error);
      alert('Failed to add area');
    }
  };

  if (loading) return <div className="loading">Loading areas...</div>;

  return (
    <div className="area-management">
      <h2>Area Management</h2>

      <div className="area-summary">
        <div className="summary-card">
          <div className="summary-icon">üèôÔ∏è</div>
          <div className="summary-content">
            <h3>Total Areas</h3>
            <div className="summary-value">{areas.length}</div>
            <p className="summary-description">Service areas configured</p>
          </div>
        </div>
      </div>

      <div className="area-list">
        <h3>Service Areas</h3>
        <div className="area-grid">
          {areas.map(area => (
            <div key={area._id} className="area-card">
              <div className="area-header">
                <div className="area-icon">üìç</div>
                <div className="area-title-section">
                  <h4 className="area-name">{area.name}</h4>
                  <span className="area-population">{area.population?.toLocaleString() || 'N/A'} residents</span>
                </div>
              </div>
              <div className="area-content">
                <p className="area-description">{area.description}</p>
                {area.emergencyServices && area.emergencyServices.length > 0 && (
                  <div className="area-services">
                    <h5>Emergency Services:</h5>
                    <div className="services-list">
                      {area.emergencyServices.map((service, index) => (
                        <span key={index} className="service-tag">{service.replace('_', ' ')}</span>
                      ))}
                    </div>
                  </div>
                )}
                {area.coordinates && (
                  <div className="area-coordinates">
                    <span className="coord-label">üìç Location:</span>
                    <span className="coord-value">
                      {typeof area.coordinates.lat === 'number' && typeof area.coordinates.lng === 'number'
                        ? `${area.coordinates.lat.toFixed(4)}, ${area.coordinates.lng.toFixed(4)}`
                        : `${area.coordinates.lat || 'N/A'}, ${area.coordinates.lng || 'N/A'}`}
                    </span>
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>
        {areas.length === 0 && (
          <div className="no-areas">No areas configured</div>
        )}
      </div>

      <Modal
        isOpen={showAddModal}
        onClose={() => setShowAddModal(false)}
        title="Add New Area"
        size="large"
      >
        <form onSubmit={handleAddArea}>
          <div className="form-row">
            <div className="form-group">
              <label>Area Name:</label>
              <input
                type="text"
                value={newArea.name}
                onChange={(e) => setNewArea({...newArea, name: e.target.value})}
                required
                placeholder="e.g., Downtown District"
              />
            </div>
            <div className="form-group">
              <label>Population:</label>
              <input
                type="number"
                value={newArea.population}
                onChange={(e) => setNewArea({...newArea, population: parseInt(e.target.value) || ''})}
                placeholder="Approximate population"
              />
            </div>
          </div>
          <div className="form-group">
            <label>Description:</label>
            <textarea
              value={newArea.description}
              onChange={(e) => setNewArea({...newArea, description: e.target.value})}
              placeholder="Area description"
              rows="3"
            />
          </div>
          <div className="form-group">
            <label>Emergency Services (comma-separated):</label>
            <input
              type="text"
              value={newArea.emergencyServices.join(', ')}
              onChange={(e) => setNewArea({...newArea, emergencyServices: e.target.value.split(',').map(s => s.trim()).filter(s => s)})}
              placeholder="e.g., fire_station, police_station, hospital"
            />
          </div>
          <div className="form-row">
            <div className="form-group">
              <label>Latitude:</label>
              <input
                type="number"
                step="0.000001"
                value={newArea.coordinates.lat}
                onChange={(e) => setNewArea({
                  ...newArea,
                  coordinates: {...newArea.coordinates, lat: parseFloat(e.target.value) || ''}
                })}
                placeholder="e.g., 40.7128"
              />
            </div>
            <div className="form-group">
              <label>Longitude:</label>
              <input
                type="number"
                step="0.000001"
                value={newArea.coordinates.lng}
                onChange={(e) => setNewArea({
                  ...newArea,
                  coordinates: {...newArea.coordinates, lng: parseFloat(e.target.value) || ''}
                })}
                placeholder="e.g., -74.0060"
              />
            </div>
          </div>
          <div className="form-actions">
            <button type="submit" className="add-btn">Add Area</button>
            <button type="button" onClick={() => setShowAddModal(false)} className="cancel-btn">Cancel</button>
          </div>
        </form>
      </Modal>

      <div className="area-actions">
        <button className="area-btn" onClick={() => setShowAddModal(true)}>Add New Area</button>
      </div>
    </div>
  );
}

export default AreaManagement;
