import React, { useState, useEffect } from 'react';
import axios from 'axios';

function AreaManagement() {
  const [areas, setAreas] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAddForm, setShowAddForm] = useState(false);
  const [newArea, setNewArea] = useState({
    name: '',
    description: '',
    population: '',
    emergencyServices: [],
    coordinates: {
      lat: '',
      lng: ''
    }
  });

  useEffect(() => {
    loadAreas();
  }, []);

  const loadAreas = async () => {
    try {
      const response = await axios.get('/api/areas');
      setAreas(response.data);
    } catch (error) {
      console.error('Error loading areas:', error);
      setAreas([]);
    } finally {
      setLoading(false);
    }
  };

  const handleAddArea = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/areas', newArea);
      setNewArea({
        name: '',
        description: '',
        population: '',
        emergencyServices: [],
        coordinates: {
          lat: '',
          lng: ''
        }
      });
      setShowAddForm(false);
      loadAreas();
      alert('Area added successfully!');
    } catch (error) {
      console.error('Error adding area:', error);
      alert('Failed to add area');
    }
  };

  if (loading) return <div className="loading">Loading areas...</div>;

  return (
    <div className="area-management">
      <h2>Area Management</h2>

      <div className="area-summary">
        <div className="area-stat">
          <h3>Total Areas</h3>
          <div className="stat-value">{areas.length}</div>
        </div>
      </div>

      <div className="area-list">
        <h3>Service Areas</h3>
        <div className="area-grid">
          {areas.map(area => (
            <div key={area._id} className="area-card">
              <div className="area-name">{area.name}</div>
              <div className="area-details">
                <p><strong>Description:</strong> {area.description}</p>
                <p><strong>Population:</strong> {area.population?.toLocaleString() || 'N/A'}</p>
                {area.emergencyServices && area.emergencyServices.length > 0 && (
                  <p><strong>Services:</strong> {area.emergencyServices.join(', ')}</p>
                )}
                {area.coordinates && (
                  <p><strong>Coordinates:</strong> {area.coordinates.lat}, {area.coordinates.lng}</p>
                )}
              </div>
            </div>
          ))}
        </div>
        {areas.length === 0 && (
          <div className="no-areas">No areas configured</div>
        )}
      </div>

      {showAddForm && (
        <div className="add-area-form">
          <h3>Add New Area</h3>
          <form onSubmit={handleAddArea}>
            <div className="form-group">
              <label>Area Name:</label>
              <input
                type="text"
                value={newArea.name}
                onChange={(e) => setNewArea({...newArea, name: e.target.value})}
                required
                placeholder="e.g., Downtown District"
              />
            </div>
            <div className="form-group">
              <label>Description:</label>
              <textarea
                value={newArea.description}
                onChange={(e) => setNewArea({...newArea, description: e.target.value})}
                placeholder="Area description"
                rows="3"
              />
            </div>
            <div className="form-group">
              <label>Population:</label>
              <input
                type="number"
                value={newArea.population}
                onChange={(e) => setNewArea({...newArea, population: parseInt(e.target.value) || ''})}
                placeholder="Approximate population"
              />
            </div>
            <div className="form-group">
              <label>Emergency Services (comma-separated):</label>
              <input
                type="text"
                value={newArea.emergencyServices.join(', ')}
                onChange={(e) => setNewArea({...newArea, emergencyServices: e.target.value.split(',').map(s => s.trim()).filter(s => s)})}
                placeholder="e.g., fire_station, police_station, hospital"
              />
            </div>
            <div className="form-group">
              <label>Latitude:</label>
              <input
                type="number"
                step="0.000001"
                value={newArea.coordinates.lat}
                onChange={(e) => setNewArea({
                  ...newArea,
                  coordinates: {...newArea.coordinates, lat: parseFloat(e.target.value) || ''}
                })}
                placeholder="e.g., 40.7128"
              />
            </div>
            <div className="form-group">
              <label>Longitude:</label>
              <input
                type="number"
                step="0.000001"
                value={newArea.coordinates.lng}
                onChange={(e) => setNewArea({
                  ...newArea,
                  coordinates: {...newArea.coordinates, lng: parseFloat(e.target.value) || ''}
                })}
                placeholder="e.g., -74.0060"
              />
            </div>
            <div className="form-actions">
              <button type="submit" className="add-btn">Add Area</button>
              <button type="button" onClick={() => setShowAddForm(false)} className="cancel-btn">Cancel</button>
            </div>
          </form>
        </div>
      )}

      <div className="area-actions">
        <button className="area-btn" onClick={() => setShowAddForm(true)}>Add New Area</button>
      </div>
    </div>
  );
}

export default AreaManagement;
