import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { format } from 'date-fns';
import './App.css';

function App() {
  const [activeTab, setActiveTab] = useState('predict');
  const [formData, setFormData] = useState({
    day_of_week: 0,
    hour_of_day: 12,
    previous_load: 50,
    seasonal_indicator: 0,
    accident_probability: 0.1
  });
  
  const [prediction, setPrediction] = useState(null);
  const [loading, setLoading] = useState(false);
  const [scenarios, setScenarios] = useState([]);
  const [history, setHistory] = useState([]);
  const [analytics, setAnalytics] = useState(null);
  const [historyLoading, setHistoryLoading] = useState(false);
  
  useEffect(() => {
    // Load demo scenarios
    axios.get('/api/scenarios')
      .then(response => setScenarios(response.data))
      .catch(error => console.error('Error loading scenarios:', error));
    
    // Load recent history
    loadHistory();
    
    // Load analytics
    loadAnalytics();
  }, []);
  
  const loadHistory = async () => {
    setHistoryLoading(true);
    try {
      const response = await axios.get('/api/predictions?limit=20');
      setHistory(response.data.predictions);
    } catch (error) {
      console.error('Error loading history:', error);
    } finally {
      setHistoryLoading(false);
    }
  };
  
  const loadAnalytics = async () => {
    try {
      const response = await axios.get('/api/analytics?days=7');
      setAnalytics(response.data);
    } catch (error) {
      console.error('Error loading analytics:', error);
    }
  };
  
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: name === 'day_of_week' || name === 'hour_of_day' || name === 'seasonal_indicator' 
        ? parseInt(value) 
        : parseFloat(value)
    });
  };
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    
    try {
      const response = await axios.post('/api/predict', formData);
      setPrediction(response.data);
    } catch (error) {
      console.error('Error getting prediction:', error);
      alert('Failed to get prediction. Please check if ML service is running.');
    } finally {
      setLoading(false);
    }
  };
  
  const loadScenario = (scenario) => {
    setFormData(scenario.params);
  };
  
  const getAlertColor = (level) => {
    switch(level) {
      case 'GREEN': return '#28a745';
      case 'YELLOW': return '#ffc107';
      case 'RED': return '#dc3545';
      default: return '#6c757d';
    }
  };
  
  const daysOfWeek = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];
  
  return (
    <div className="App">
      <header className="App-header">
        <h1>Hospital Emergency Load Intelligence System</h1>
        <p>Predictive Resource Management Dashboard</p>
      </header>
      
      <div className="container">
        <div className="form-section">
          <h2>Predict Emergency Load</h2>
          
          <div className="scenarios">
            <h3>Demo Scenarios:</h3>
            {scenarios.map((scenario, index) => (
              <button 
                key={index} 
                onClick={() => loadScenario(scenario)}
                className="scenario-btn"
              >
                {scenario.name}
              </button>
            ))}
          </div>
          
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Day of Week:</label>
              <select name="day_of_week" value={formData.day_of_week} onChange={handleInputChange}>
                {daysOfWeek.map((day, index) => (
                  <option key={index} value={index}>{day}</option>
                ))}
              </select>
            </div>
            
            <div className="form-group">
              <label>Hour of Day (0-23):</label>
              <input 
                type="number" 
                name="hour_of_day" 
                min="0" 
                max="23" 
                value={formData.hour_of_day} 
                onChange={handleInputChange} 
              />
            </div>
            
            <div className="form-group">
              <label>Previous Emergency Load:</label>
              <input 
                type="number" 
                name="previous_load" 
                min="0" 
                value={formData.previous_load} 
                onChange={handleInputChange} 
              />
            </div>
            
            <div className="form-group">
              <label>Seasonal Indicator (0=Normal, 1=Peak):</label>
              <select name="seasonal_indicator" value={formData.seasonal_indicator} onChange={handleInputChange}>
                <option value={0}>Normal</option>
                <option value={1}>Peak Season</option>
              </select>
            </div>
            
            <div className="form-group">
              <label>Accident/Outbreak Probability (0.0-1.0):</label>
              <input 
                type="number" 
                name="accident_probability" 
                min="0" 
                max="1" 
                step="0.01" 
                value={formData.accident_probability} 
                onChange={handleInputChange} 
              />
            </div>
            
            <button type="submit" disabled={loading} className="predict-btn">
              {loading ? 'Predicting...' : 'Get Prediction'}
            </button>
          </form>
        </div>
        
        {prediction && (
          <div className="results-section">
            <h2>Prediction Results</h2>
            
            <div className="alert-section" style={{ backgroundColor: getAlertColor(prediction.alert_level) }}>
              <h3>Hospital Readiness Status: {prediction.alert_level}</h3>
              <div className="recommendations">
                <h4>AI Recommendations:</h4>
                <ul>
                  {prediction.recommendations.map((rec, index) => (
                    <li key={index}>{rec}</li>
                  ))}
                </ul>
              </div>
            </div>
            
            <div className="metrics-grid">
              <div className="metric-card">
                <h3>Emergency Load</h3>
                <div className="metric-value">{prediction.emergency_load}</div>
                <p>patients expected</p>
              </div>
              
              <div className="metric-card">
                <h3>ICU Beds Required</h3>
                <div className="metric-value">{prediction.icu_beds_required}</div>
                <p>beds needed</p>
              </div>
              
              <div className="metric-card">
                <h3>Ventilator Demand</h3>
                <div className="metric-value">{prediction.ventilator_demand}</div>
                <p>ventilators required</p>
              </div>
              
              <div className="metric-card">
                <h3>Staff Workload</h3>
                <div className="metric-value">{prediction.staff_workload}</div>
                <p>workload level</p>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
